mapper.insert:
insert into sys_role (id, role_name, hospital_id
  )
values (#{id,jdbcType=BIGINT}, #{role_name,jdbcType=VARCHAR}, #{hospital_id,jdbcType=BIGINT}
  )

mapper.update:
update sys_role
set role_name = #{role_name,jdbcType=VARCHAR},
  hospital_id = #{hospital_id,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, role_name, hospital_id
from sys_role
where id = #{id,jdbcType=BIGINT}
